import React, { useState } from 'react';
import axios from 'axios';

const CongressTrades = () => {
  const [inputValue, setInputValue] = useState('');
  const [congressMember, setCongressMember] = useState('');
  const [trades, setTrades] = useState([]);
  const [visibleTrades, setVisibleTrades] = useState({});
  const [error, setError] = useState('');
  const [dataStructure, setDataStructure] = useState(null); // State to track the chosen data structure

  const handleSearch = async () => {
    console.log('Initiating search for:', inputValue);
    try {
      const response = await axios.get('/search', {
        params: { term: inputValue, data_structure: dataStructure }, // Pass chosen data structure
      });
      console.log('Search results:', response.data);
      if (response.data.length > 0) {
        fetchTrades(response.data[0]);
      } else {
        setTrades([]);
        setError('Congress person not found.');
        setCongressMember('');
      }
    } catch (error) {
      console.error('Error fetching matches:', error);
      setError('Error fetching data. Please try again later.');
      setTrades([]);
      setCongressMember('');
    }
  };

  const fetchTrades = async (name) => {
    console.log('Fetching trades for:', name);
    try {
      const response = await axios.get(`/trades/${name}`, {
        params: { data_structure: dataStructure }, // Pass chosen data structure
      });
      console.log('Trades fetched:', response.data);
      setTrades(response.data);
      setCongressMember(name);
      setError('');
    } catch (error) {
      console.error('Error fetching trades:', error);
      setError('Error fetching trades. Please try again later.');
      setTrades([]);
    }
  };

  const toggleTradeVisibility = (ticker) => {
    setVisibleTrades((prevVisibleTrades) => {
      const newVisibleTrades = {
        ...prevVisibleTrades,
        [ticker]: !prevVisibleTrades[ticker],
      };
      console.log('Visibility Toggled for:', ticker, newVisibleTrades[ticker]);
      return newVisibleTrades;
    });
  };
  

  return (
    <div>
      <h1>Capitol Trades</h1>
      {!dataStructure && (
        <div style={{ marginLeft: '20px', marginBottom: '10px' }}>
          <button onClick={() => setDataStructure('list')}>Use Adjacency List</button>
          <button onClick={() => setDataStructure('matrix')}>Use Adjacency Matrix</button>
        </div>
      )}
      {dataStructure && (
        <>
          <p style={{ marginLeft: '20px', marginBottom: '10px' }}>
            Enter a congress member:
          </p>
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            style={{ marginLeft: '20px', marginBottom: '10px' }}
          />
          <button onClick={handleSearch}>Search</button>
          {error && <p>{error}</p>}
          {congressMember && (
            <h2 style={{ marginLeft: '20px', marginBottom: '20px' }}>
              List of Stock Trades for {congressMember}
            </h2>
          )}
          <div
              style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',  // Increased minmax lower bound for wider buttons
                gap: '20px',  // Increased gap for more space between buttons
                marginLeft: '20px',
                marginRight: '20px',
              }}              
            >
              {Object.keys(trades).length > 0 &&
                Object.entries(trades).map(([ticker, tradeDetails], index) => (
                  <div
                    key={`${ticker}-${visibleTrades[ticker]}`} // Changed key to force re-render on visibility toggle
                    style={{
                      position: 'relative',
                      overflow: 'visible', // Ensuring overflow isn't hidden
                    }}
                  >
                    <button
                      onClick={() => toggleTradeVisibility(ticker)}
                      style={{
                        width: '100%',
                        height: '40px',
                        borderRadius: '5px',
                        background: '#efefef',
                        border: 'none',
                        boxShadow: '0px 2px 5px rgba(0, 0, 0, 0.2)',
                        cursor: 'pointer',
                        transition: 'transform 0.3s',
                        overflow: 'hidden',
                        whiteSpace: 'nowrap',
                        textOverflow: 'ellipsis',
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = '#d9d9d9';
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = '#efefef';
                      }}
                    >
                      {ticker}
                    </button>
                    {visibleTrades[ticker] && (
                      <div
                        style={{
                          position: 'absolute',
                          top: 'calc(100% + 5px)',
                          left: '0',
                          right: '0',  // Ensures the container stretches to the full width of the parent
                          background: '#ffffff',
                          boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.25)',
                          padding: '15px',  // Increased padding for better spacing
                          borderRadius: '5px',  // Rounded corners for better visual appeal
                          zIndex: '10',
                        }}
                      >
                        {tradeDetails.length > 0 && <strong>{tradeDetails[0].name}</strong>}
                        <ul>
                          {tradeDetails.map((trade, idx) => (
                            <li key={idx} style={{ background: '#f7f7f7', margin: '5px 0', padding: '10px', borderRadius: '5px' }}>
                              <div>
                                Date: {trade.date} | Type: {trade.trade_type ? trade.trade_type.toUpperCase() : 'N/A'} | Amount: {trade.amount}
                              </div>
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                ))}
            </div>
        </>
      )}
    </div>
  );
};

export default CongressTrades;
